function moveTimeline(percentage,objTimeline){var range=objTimeline.getWindow();var interval=range.end-range.start;objTimeline.setWindow({start:range.start.valueOf()-interval*percentage,end:range.end.valueOf()-interval*percentage});}
function zoomTimeline(percentage,objTimeline){var range=objTimeline.getWindow();var interval=range.end-range.start;objTimeline.setWindow({start:range.start.valueOf()-interval*percentage,end:range.end.valueOf()+interval*percentage});}